#include "dexterind-pressure.h"

task main() {
  float pressure;
  byte state = 0;

  displayTextLine(0, "Dexter Industries");
  displayCenteredTextLine(1, "dPressure 250");
  displayCenteredTextLine(3, "Test 1");
  displayCenteredTextLine(5, "Connect sensor");
  displayCenteredTextLine(6, "to S1");
  sleep(2000);
  eraseDisplay();

  displayTextLine(0, "Dexter Industries");
  displayCenteredTextLine(7, "< switch scale >");
  //loop to read temp
  while (true) {
    switch(nNxtButtonPressed) {
      // If the right button is pressed, cycle through the scales
      case kRightButton:
        if (++state > 1)
          state = 0;
        while (nNxtButtonPressed != kNoButton) sleep(1);
        break;

        // If the left button is pressed, cycle through the scales in reverse
      case kLeftButton:
        if (--state < 0)
          state = 1;
        // debounce the button
        while (nNxtButtonPressed != kNoButton) sleep(1);
        break;
    }

    displayCenteredBigTextLine(1, "Pressure:");
    switch(state) {
      // if state: 0, display temp in degrees celcius
      case 0: DPRESSreadPress250kPa(DPRESS, pressure);
              displayCenteredBigTextLine(3, "%4.2f", pressure);
              displayCenteredBigTextLine(5, "kPa");
              break;

      // if state: 1, display temp in Fahrenheit
      case 1: DPRESSreadPress250PSI(DPRESS, pressure);
              displayCenteredBigTextLine(3, "%4.2f", pressure);
              displayCenteredBigTextLine(5, "PSI.");
              break;
    }
    sleep(10);
  }
}
