#include "hitechnic-protoboard.h"

task main() {
  // Local variables
  short inputdata;

  while(true) {
    // Read a 10bit wide analogue value from A0
    inputdata = HTPBreadADC(HTPB, 0, 10);

    eraseDisplay();
    displayTextLine(1, "%d", inputdata);

    sleep(50);
  }
}
