#pragma config(Sensor, S2,     gyro,           sensorEV3_Gyro, modeEV3Gyro_RateAndAngle)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

task main()
{
	// Array to hold the maximum number of values a dataset can return
	float dataSet[8];

	// The actual number of values that the specific mode returns
	long dataSetSize;

	while (true)
	{
		// Some sensors modes like modeEV3Gyro_RateAndAngle will
	  // return multiple values, which can be retrieved with getSensorDataset()
	  // A dataset can be up to 8 floats, so make sure your array is appropriately sized.
	  // For the gyro, the first element in the dataset is rate, the next one is the current angle
		getSensorDataset(gyro, &dataSet[0], dataSetSize);
		displayCenteredBigTextLine(1, "Dataset size: %d", dataSetSize);
		displayCenteredBigTextLine(4, "Rate: %2.0f", dataSet[1]);
		displayCenteredBigTextLine(6, "Angle: %2.0f", dataSet[0]);

		// Wait 20 ms to get 50 readings per second
		sleep(20);
	}
}
